import React from 'react';
import Model from './model/Model.js';
import { redrawCanvas } from './boundary/Boundary.js'
import { layout } from './Layout.js'
import { adjust } from './controller/Controller.js'

function App() {

  const [model, setModel] = React.useState(new Model(5, 10, 15));

  const appRef = React.useRef(null);      // Later need to be able to refer to App 
  const canvasRef = React.useRef(null);   // Later need to be able to refer to Canvas

  /** Ensures initial rendering is performed, and that whenever model changes, it is re-rendered. */
  React.useEffect (() => {

    /** Happens once. */
    redrawCanvas(model, canvasRef.current, appRef.current);
  }, [model])   // this second argument is CRITICAL, since it declares when to refresh (whenever Model changes)

  const adjustController = (dirction) => {
    let newModel = adjust(model, direction);

    setModel(newModel);   // react to changes, if model has changed.
  }

  return (
    <main style={layout.Appmain} ref={appRef}>
      <canvas className="{layout.canvas}"  
        ref={canvasRef}
        width={layout.canvas.width}
        height={layout.canvas.height}>

      </canvas>
      <label style={layout.text}>{"Width: " + model.value}</label>
      <div style={layout.buttons}>
        <button style={layout.upbutton}   onClick={(e) => adjustController(+1)}>^</button>
        <button style={layout.downbutton} onClick={(e) => adjustController(-1)}>v</button>
      </div>
    </main>
  );
}

export default App;
