import { config_4x4, config_5x5, config_6x6 } from "./config";

// someone needs to know about these configurations. Perhaps we should!
const configs = [config_5x5, config_4x4, config_6x6]


// wouldn't it be useful to have a Square class?
export class Square {
    constructor(row, column) {
        this.row = row
        this.column = column
    }
}

export class Board {
    constructor(size) {
        this.size = size

        this.grid = Array.from(Array(size), () => new Array(size));

        for (let r = 0; r < size; r++) {
            for (let c = 0; c < size; c++) {
                this.grid[r][c] = new Square(r, c)
            }
        }

        // how you access a square by its [row][column] location
        // this.grid[2][4]
    }
    // Check if a 2x2 block starting at (row, column) is all the same color
    is2x2BlockSameColor(row, column) {
        const firstColor = this.grid[row][column].color;
        if (row + 1 >= this.size || column + 1 >= this.size) {
            return false; // Out of bounds, cannot be a 2x2 block
        }

        return this.grid[row][column + 1].color === firstColor &&
            this.grid[row + 1][column].color === firstColor &&
            this.grid[row + 1][column + 1].color === firstColor;
    }

    // Remove a 2x2 block by changing its color to white
    remove2x2Block(row, column) {
        for (let r = row; r < row + 2; r++) {
            for (let c = column; c < column + 2; c++) {
                this.grid[r][c].color = 'white';
            }
        }
    }
}

// a method that might be useful
