import React from 'react';
import './App.css';

import Model from './model/Model.js';
import { redrawCanvas } from './boundary/Boundary.js'

function App() {
  // initial instantiation of the Model
  const [model, setModel] = React.useState(new Model(0));  // only place where Model object is instantiated.
  const [redraw, forceRedraw] = React.useState(0);    // change values to force redraw

  const appRef = React.useRef(null);      // Later need to be able to refer to App 
  const canvasRef = React.useRef(null);   // Later need to be able to refer to Canvas

  /** Ensures initial rendering is performed, and that whenever model changes, it is re-rendered. */
  React.useEffect (() => {

    /** Happens once. */
    redrawCanvas(model, canvasRef.current, appRef.current);
  }, [model, redraw])   // this second argument is CRITICAL, since it declares when to refresh (whenever Model changes)

  // controller to handle moving
  const moveNinjaSe = (direction) => {
    movePiece(model, direction);
    model.board.grid[1][3].color = 'red'
    forceRedraw(redraw+1)   // react to changes, if model has changed.
  }

  return (
    <div className="App" ref={appRef}>
      <canvas tabIndex="1"  
        data-testid="canvas"
        className="App-canvas"
        ref={canvasRef}
        width={500}
        height={500}
        />

      { model.isVictorious() ? (<label data-testid="victory-label" style={layout.victory}>YOU WIN CONGRATULATIONS!</label>) : null }

<label data-testid="moves-label" style={layout.text}>{"number moves: " + model.numMoves}</label>
<div style={layout.buttons}>
   <button data-testid="upbutton" style={layout.upbutton}     onClick={(e) => movePieceHandler(Up)}       >UP</button>
   <button data-testid="leftbutton" style={layout.leftbutton}   onClick={(e) => movePieceHandler(Left)}  >LEFT</button>
   <button data-testid="rightbutton" style={layout.rightbutton}  onClick={(e) => movePieceHandler(Right)}  >RIGHT</button>
   <button data-testid="downbutton" style={layout.downbutton}   onClick={(e) => movePieceHandler(Down)}   >DOWN</button>
</div>

       <button className="upbutton" data-testid="upbutton"   onClick={(e) => moveNinjaSe(1)}   >^</button>
       <button className="downbutton" data-testid="downbutton"   onClick={(e) => moveNinjaSe(3)}   >v</button>

    </div>
  );
}

export default App;
