/** Redraw entire canvas from model. */
export function redrawCanvas(model, canvasObj, appObj) {
    const ctx = canvasObj.getContext('2d');

    // example to show that you can get style information from app
    ctx.fillStyle = window.getComputedStyle(appObj).backgroundColor;
    ctx.fillRect( 0,0, canvasObj.width, canvasObj.height); 

    // fill in unnecessary areas.
    let v = model.value;

    ctx.fillStyle = 'yellow';
    ctx.fillRect( 100, 100, 10*v, 10*v);

    ctx.beginPath();
    ctx.strokeStyle = 'red';
    ctx.rect( 100, 100, 10*model.maxValue, 10*model.maxValue)
    ctx.stroke();
}
