import { config_4x4, config_5x5, config_6x6 } from "./config";

// someone needs to know about these configurations. Perhaps we should!
const configs = [ config_5x5, config_4x4, config_6x6 ]

export class MoveType {
    constructor(dr, dc) {
        this.deltar = dr;
        this.deltac = dc;
    }

    static parse(s) {
        if ((s === "down")  || (s === "Down"))   { return Down; }
        if ((s === "up")    || (s === "Up"))     { return Up; }
        if ((s === "left")  || (s === "Left"))   { return Left; }
        if ((s === "right") || (s === "Right"))  { return Right; }

        return NoMove;
    }
}

export const Down = new MoveType(1, 0, "down");
export const Up = new MoveType(-1, 0, "up");
export const Left = new MoveType(0, -1, "left");
export const Right = new MoveType(0, 1, "right");
export const NoMove = new MoveType(0, 0, "*");  // no move is possible

// wouldn't it be useful to have a Square class?
export class Square {
    constructor(row, column) {
        this.row = row
        this.column = column
    }
}

export class Board {
    constructor (size) {
        this.size = size

        this.grid = Array.from(Array(size), () => new Array(size));

        for (let r = 0; r < size; r++) {
            for (let c = 0; c < size; c++) {
                this.grid[r][c] = new Square(r,c)

                // a bit of a hack just to add some color
                if (r % 2 == 0 && c % 2 == 0) {
                    this.grid[r][c].color = 'gray'
                } else {
                    this.grid[r][c].color = 'black'
                }
            }
        }

	// how you access a square by its [row][column] location
        // this.grid[2][4]
    }




