// Check surrounding 2x2 blocks for a match
function is2x2BlockSameColor(row, column, boardSize) {
  // Get the color of the clicked square
  const clickedColor = model.board.grid[row][column]?.color;

  // If the clicked square is undefined (or white), there cannot be a 2x2 block of the same color
  if (!clickedColor) {
    return null;
  }

  // Define the directions to check around the clicked square for the top-left corner of a 2x2 block
  const topLeftCorners = [
    { dr: -1, dc: -1 }, // top-left corner is diagonal up-left
    { dr: -1, dc: 0 },  // top-left corner is directly above
    { dr: 0, dc: -1 },  // top-left corner is directly to the left
    { dr: 0, dc: 0 },   // clicked square is the top-left corner
  ];

  // Check each possible top-left corner for a 2x2 block
  for (let corner of topLeftCorners) {
    const topLeftRow = row + corner.dr;
    const topLeftCol = column + corner.dc;

    // Check if the top-left corner is within bounds
    if (topLeftRow < 0 || topLeftCol < 0 || topLeftRow + 1 >= boardSize || topLeftCol + 1 >= boardSize) {
      continue; // Skip this corner, it's out of bounds
    }

    // Assume this is a valid block until proven otherwise
    let isValidBlock = true;
    let matchCount = 0;

    // Check the 2x2 block from this top-left corner
    for (let dr = 0; dr <= 1; dr++) {
      for (let dc = 0; dc <= 1; dc++) {
        const checkRow = topLeftRow + dr;
        const checkCol = topLeftCol + dc;
        const checkColor = model.board.grid[checkRow][checkCol]?.color;

        // If the color matches the clicked color, increase the match count
        if (checkColor === clickedColor) {
          matchCount++;
        }
      }
    }

    // If there are at least 3 matching squares, return the top-left corner of this block
    if (matchCount >= 3) {
      return { row: topLeftRow, column: topLeftCol };
    }
  }

  // If no matching 2x2 block is found
  return null;
}


