              // Push the colored squares upwards
              for (let r = model.ninjaRow - 2; r >= 0; r--) {
                  if (model.board.grid[r][model.ninjaColumn].color === 'white') {
                      model.board.grid[r][model.ninjaColumn].color = model.board.grid[r + 1][model.ninjaColumn].color;
                      model.board.grid[r + 1][model.ninjaColumn].color = 'white';
