        if (model.ninjaRow != model.config.numRows-2){

          model.ninjaRow++;

            model.numMoves++;

          }
            break;
        case "Left":
        if (model.ninjaColumn != 0){

          model.ninjaColumn--;

            model.numMoves++;

      }
      break;
      }
